import React, { useState } from "react";
import axios from "axios";

const App = () => {
  const [resumeResults, setResumeResults] = useState(null);

  const analyzeResume = async () => {
    const response = await axios.post("http://localhost:5000/analyze-resume", {
      filePath: "resume.pdf"
    });
    setResumeResults(response.data.results);
  };

  return (
    <div>
      <h1>Career Recommendations & Resume Screener</h1>
      <button onClick={analyzeResume}>Analyze Resume</button>
      {resumeResults && resumeResults.map((result, index) => (
        <div key={index}>
          <h4>Role: {result.role}</h4>
          <p><strong>Matched Keywords:</strong> {result.matchedKeywords.join(", ")}</p>
          <p><strong>Confidence Score:</strong> {result.confidence}%</p>
          <p><strong>Job Fit Score:</strong> {result.jobFitScore}%</p>
        </div>
      ))}
    </div>
  );
};

export default App;
