import React, { useState } from 'react';
import axios from 'axios';

const CareerRecommendations = () => {
    const [data, setData] = useState({
        name: '',
        academicPerformance: '',
        interests: '',
        skills: ''
    });
    const [recommendations, setRecommendations] = useState(null);

    const handleChange = (e) => {
        setData({ ...data, [e.target.name]: e.target.value });
    };

    const getRecommendations = async () => {
        const response = await axios.post('http://localhost:5000/recommendations', data);
        setRecommendations(response.data);
    };

    return (
        <div>
            <h2>Get Career Recommendations</h2>
            <input name="name" placeholder="Name" onChange={handleChange} />
            <input name="academicPerformance" placeholder="Academic Performance" onChange={handleChange} />
            <input name="interests" placeholder="Interests (comma separated)" onChange={handleChange} />
            <input name="skills" placeholder="Skills (comma separated)" onChange={handleChange} />
            <button onClick={getRecommendations}>Get Recommendations</button>
            {recommendations && <pre>{JSON.stringify(recommendations, null, 2)}</pre>}
        </div>
    );
};

export default CareerRecommendations;
