import React, { useState } from "react";
import axios from "axios";
import "./ResumeScreener.css"; // Import CSS file

const ResumeScreener = () => {
    const [file, setFile] = useState(null);
    const [result, setResult] = useState(null);

    const handleFileChange = (e) => {
        setFile(e.target.files[0]);
    };

    const handleSubmit = async () => {
        const formData = new FormData();
        formData.append("resume", file);

        try {
            const response = await axios.post("http://localhost:5000/api/resume", formData);
            setResult(response.data);
        } catch (error) {
            console.error("Error analyzing resume:", error);
        }
    };

    return (
        <div className="resume-container">
            <h2 className="section-title">üìÑ Resume Screening</h2>

            <div className="upload-box">
                <input type="file" onChange={handleFileChange} />
                <button onClick={handleSubmit}>Analyze Resume</button>
            </div>

            {result && (
                <div className="result-card">
                    <h3>üìä Resume Analysis Results:</h3>
                    {result.roles.map((roleData, index) => (
                        <div key={index} className="role-box">
                            <p><b>üéØ Role:</b> {roleData.role}</p>
                            <p><b>‚úÖ Matched Skills:</b> {roleData.matchedSkills.join(", ")}</p>
                            <p><b>üèÜ Confidence Score:</b> {roleData.confidenceScore}%</p>
                            <p><b>üí° Job Fit Score:</b> {roleData.jobFitScore}%</p>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default ResumeScreener;
