import React, { useState } from "react";
import axios from "axios";

const ResumeScreener = () => {
    const [file, setFile] = useState(null);
    const [role, setRole] = useState(""); 
    const [skillsRequired, setSkillsRequired] = useState(""); 
    const [result, setResult] = useState(null);
    const [error, setError] = useState("");

    const handleFileChange = (e) => {
        setFile(e.target.files[0]);
    };

    const handleSubmit = async () => {
        if (!file || !role || !skillsRequired) {
            setError("Please provide all inputs!");
            return;
        }
        setError(""); 

        const formData = new FormData();
        formData.append("resume", file);
        formData.append("role", role);
        formData.append("skillsRequired", skillsRequired);

        try {
            const response = await axios.post("http://localhost:5000/api/analyze-resume", formData);
            setResult(response.data);
        } catch (error) {
            setError("Error analyzing resume");
        }
    };

    return (
        <div>
            <h2>Resume Screening</h2>
            <input type="text" placeholder="Enter Role" value={role} onChange={(e) => setRole(e.target.value)} />
            <input type="text" placeholder="Required Skills (comma separated)" value={skillsRequired} onChange={(e) => setSkillsRequired(e.target.value)} />
            <input type="file" onChange={handleFileChange} />
            <button onClick={handleSubmit}>Analyze Resume</button>

            {error && <p style={{ color: "red" }}>{error}</p>}

            {result && (
                <div>
                    <h3>Resume Analysis Results:</h3>
                    <p><b>Role:</b> {result.role}</p>
                    <p><b>Matched Skills:</b> {result.matchedSkills.join(", ")}</p>
                    <p><b>Job Fit Score:</b> {result.jobFitScore}</p>
                </div>
            )}
        </div>
    );
};

export default ResumeScreener;
